<script lang="ts">
    import {Component, Emit, Vue} from 'vue-property-decorator'
    import RadioButton from "@/components/RadioButton.vue";
    import {SessionLogTag} from "@/Types/SessionLogTag";

    @Component(
        {
            components: {RadioButton},
            computed: {tags : () => SessionLogTag}
        }
    )
    export default class TagFilterList extends Vue {


        protected isSelected(t:number):boolean {
            return (this.selectedTags & t) == t;
        }
        
        private selectedTags: number = 1;
        
        public static isSelected(tag:number):boolean {
            return true;
        }
        
        @Emit('change')
        private filterTag(tag: number) {
            
            if((this.selectedTags & tag) > 0)
                this.selectedTags = this.selectedTags - tag;
            else
                this.selectedTags = this.selectedTags + tag;
            
            return this.selectedTags;
        }

        @Emit('change')
        public clear() {
            this.selectedTags = 0;
            return this.selectedTags;
        }

    }
</script>

